var Navcontroller=new ScrollMagic.Controller;let navBackground=document.querySelector("nav"),startaproject=document.querySelector("nav a.startaproject"),whiteSections=document.querySelectorAll(".whiteSection");function ChangeNav(){whiteSections.forEach(t=>{let e={triggerElement:t,duration:t.clientHeight,triggerHook:.1};new ScrollMagic.Scene(e).on("enter",function(){navBackground.classList.add("bg-orange2"),navBackground.classList.remove("bg-transparent"),startaproject.classList.add("textGray"),startaproject.classList.remove("text-white")}).on("leave",function(){navBackground.classList.remove("bg-orange2"),navBackground.classList.add("bg-transparent"),startaproject.classList.add("text-white"),startaproject.classList.remove("textGray")}).addTo(Navcontroller)})}onload=()=>{ChangeNav()};